using UnityEngine;
using System.Collections;
using System.Collections.Generic;

public class UI_NPC_Package : Controller {

	private Dictionary<int, UI_Character_Package_Page> mPageList = new Dictionary<int, UI_Character_Package_Page>();
	 
	public Transform  mInstanceNode;
    public GameObject mPagePrefab;
    public int mPageSlotMaxCount = 30;

	protected Game_Storage_Manager_Base mFather;
    void Start()
    {

    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
    public UI_Package_Slot_Base GetSlotWithPos(int Pos)
    {
        if (mPageList.ContainsKey((Pos) / mPageSlotMaxCount))
            return mPageList[(Pos) / mPageSlotMaxCount].GetSlotWithPos(Pos);
        return null;
    }

	public void InitPackageWithStroage(Game_Storage_Manager_Base Target)
    {
		for (int i = 0; i <= mFather.mStorageSlotCount / (mPageSlotMaxCount + 1); i++)
        {
            UI_Character_Package_Page tmpPage = (Instantiate(mPagePrefab) as GameObject).GetComponent<UI_Character_Package_Page>();
            tmpPage.transform.parent = mInstanceNode;
            tmpPage.name = i + "";
            tmpPage.transform.localScale = Vector3.one;
            tmpPage.transform.localEulerAngles = Vector3.zero;
			tmpPage.InitWithStartSlotID(Target, i * mPageSlotMaxCount, mPageSlotMaxCount);
            mPageList.Add(i, tmpPage);
        }
        mInstanceNode.GetComponent<UIGrid>().Reposition();
		mInstanceNode.GetComponent<UICenterOnChild>().Recenter();
    }

    public void InsertPackage(int Pos, Game_Item_Base Target)
    {
        mPageList[(Pos) / mPageSlotMaxCount].InsertPageWithItem(Pos, Target);
    }
}
